import Layout from "../../../components/layout.component";
import withAdmin from "../../withAdmin";
import { useState, useEffect } from "react";
import axios from "axios";
import { API } from "../../../config";
import { showSuccessMessage, showErrorMessage } from "../../../utils/alerts";
import Resizer from "react-image-file-resizer";

const Create = ({ user, token }) => {
  const [state, setState] = useState({
    name: "",
    content: "",
    error: "",
    success: "",
    buttonText: "Create",
    imageUploadText: "Upload image",
    image: "",
  });

  const {
    name,
    content,
    success,
    error,
    buttonText,
    imageUploadText,
    image,
  } = state;

  const handleChange = (name) => (e) => {
    setState({
      ...state,
      [name]: e.target.value,
      error: "",
      success: "",
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setState({
      ...state,
      buttonText: "Creating",
    });
    try {
      const response = await axios.post(`${API}/category`, {name, content, image}, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      console.log(`response category`, response);
      setState({
        ...state,
        name: "",
        buttonText: "Created",
        imageUploadText: "Upload image",
      });
    } catch (error) {
      console.log(`response category error`, error);
      setState({
        ...state,
        name: "",
        buttonText: "Create",
        error: error.res,
      });
    }
  };

  const handleImage = (e) => {
    var fileInput = false;
    if (e.target.files[0]) {
      fileInput = true;
    }
    if (fileInput) {
      Resizer.imageFileResizer(
        e.target.files[0],
        300,
        300,
        "JPEG",
        100,
        0,
        (uri) => {
          setState({
            ...state,
            image: uri,
          });
        },
        "base64"
      );
    }
  };

  const createCategoryForm = () => (
    <form onSubmit={handleSubmit}>
      <div className="form-group">
        <label className="text-mited">Name</label>
        <input
          onChange={handleChange("name")}
          value={name}
          type="text"
          className="form-control"
        ></input>
      </div>
      <div className="form-group">
        <label className="text-mited">Content</label>
        <textarea
          onChange={handleChange("content")}
          value={content}
          className="form-control"
        ></textarea>
      </div>
      <div className="form-group">
        <label className="btn btn-outline-secondary">
          Text
          <input
            onChange={handleImage}
            type="file"
            className="form-control"
            accept="image/*"
            hidden
          ></input>
        </label>
      </div>
      <div>
        <button className="btn btn-outline-warning">{buttonText}</button>
      </div>
    </form>
  );

  return (
    <Layout>
      <div className="row">
        <div className="col-md-6 offset-md-3">
          <h1>Create category</h1>
          <br />
          {createCategoryForm()}
        </div>
      </div>
    </Layout>
  );
};

export default withAdmin(Create);
