import { useState, useEffect } from "react";
import Layout from "../../../components/layout.component";
import axios from "axios";
import { API } from "../../../config";

import { showSuccessMessage, showErrorMessage } from "../../../utils/alerts";
import { getCookie } from "../../../utils/auth";

const Create = ({ token }) => {
  const [state, setState] = useState({
    title: "",
    url: "",
    categories: [],
    loadedCategories: [],
    success: "",
    error: "",
    type: "",
    medium: "",
  });

  const {
    title,
    url,
    categories,
    loadedCategories,
    success,
    error,
    type,
    medium,
  } = state;

  useEffect(() => {
    loadCategories();
  }, [success]);

  const loadCategories = async () => {
    const response = await axios.get(`${API}/categories`);
    console.log("loadCategories:", response.data);
    setState({
      ...state,
      loadedCategories: response.data,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    console.table({ title, url, categories, type, medium });

    try {
      const response = await axios.post(
        `${API}/link`,
        {
          title,
          url,
          categories,
          type,
          medium,
        },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      setState({
        ...state,
        title: "",
        url: "",
        success: "Link is created",
        error: "",
        loadedCategories: [],
        categories: [],
        type: "",
        medium: "",
      });
    } catch (error) {
      console.log("error submit link", error);
      setState({
        ...state,
        error: error.response.data.error,
      });
    }
  };

  const handleTitleChange = (e) => {
    setState({
      ...state,
      title: e.target.value,
      error: "",
      success: "",
    });
  };

  const handleUrlChange = (e) => {
    setState({
      ...state,
      url: e.target.value,
      error: "",
      success: "",
    });
  };

  const handleTypeClick = (e) => {
    setState({
      ...state,
      type: e.target.value,
      success: "",
      error: "",
    });
  };

  const showTypes = () => (
    <React.Fragment>
      <div className="form-check ml-3">
        <label className="form-check-label">
          <input
            type="radio"
            onClick={handleTypeClick}
            checked={type === "free"}
            value="free"
            className="form-check-input"
            name="type"
          />
          Free{" "}
        </label>
      </div>
      <div className="form-check ml-3">
        <label className="form-check-label">
          <input
            type="radio"
            onClick={handleTypeClick}
            checked={type === "paid"}
            value="paid"
            className="form-check-input"
            name="type"
          />{" "}
          Paid
        </label>
      </div>
    </React.Fragment>
  );

  const handleMediumClick = (e) => {
    setState({
      ...state,
      medium: e.target.value,
      success: "",
      error: "",
    });
  };

  const showMedium = () => (
    <React.Fragment>
      <div className="form-check ml-3">
        <label className="form-check-label">
          <input
            type="radio"
            onClick={handleMediumClick}
            checked={medium === "video"}
            value="video"
            className="form-check-input"
            name="medium"
          />
          Video{" "}
        </label>
      </div>
      <div className="form-check ml-3">
        <label className="form-check-label">
          <input
            type="radio"
            onClick={handleMediumClick}
            checked={medium === "book"}
            value="book"
            className="form-check-input"
            name="medium"
          />
          Book{" "}
        </label>
      </div>
    </React.Fragment>
  );

  const handleToogle = (id) => () => {
    const index = categories.indexOf(id);
    const all = [...categories];

    if (index === -1) {
      all.push(id);
    } else {
      all.splice(index, 1);
    }
    console.log("categories: ", all);
    setState({
      ...state,
      categories: all,
      success: "",
      error: "",
    });
  };

  const showCategories = () => {
    return (
      loadedCategories &&
      loadedCategories.map((category, index) => (
        <li className="list-unstyled" key={category._id}>
          <input
            type="checkbox"
            onChange={handleToogle(category._id)}
            className="mr-2"
          />
          <label className="form-check-label">{category.name}</label>
        </li>
      ))
    );
  };

  const submitLinkForm = () => (
    <form onSubmit={handleSubmit}>
      <div className="form-group">
        <label className="text-muted">Title</label>
        <input
          type="text"
          className="form-control"
          onChange={handleTitleChange}
          value={title}
        />
      </div>
      <div className="form-group">
        <label className="text-muted">URL</label>
        <input
          type="url"
          className="form-control"
          onChange={handleUrlChange}
          value={url}
        />
      </div>
      <div>
        <button className="btn btn-outline-warning" type="submit">
          Submit
        </button>
      </div>
    </form>
  );

  return (
    <Layout>
      <div className="row">
        <div className="col-md-12">
          <h1>Submit Link/URL</h1>
          <br />
        </div>
      </div>
      <div className="row">
        <div className="col-md-4">
          <div className="form-group">
            <label className="text-muted ml-4">Category</label>
            <ul style={{ maxHeight: "100px", overflowY: "scroll" }}>
              {showCategories()}
            </ul>
          </div>
          <div className="form-group">
            <label className="text-muted ml-4">Type</label>
            {showTypes()}
          </div>
          <div className="form-group">
            <label className="text-muted ml-4">Medium</label>
            {showMedium()}
          </div>
        </div>
        <div className="col-md-8">{submitLinkForm()}</div>
      </div>
    </Layout>
  );
};

Create.getInitialProps = ({ req }) => {
  const token = getCookie("token", req);
  return { token };
};

export default Create;
