import Layout from "../components/layout.component";
import { useState } from "react";
import axios from "axios";
import {showSuccessMessage, showErrorMessage} from '../components/alerts.component'

const Register = () => {
  const [state, setState] = useState({
    name: "",
    email: "",
    password: "",
    error: "",
    success: "",
    buttonText: "Register",
  });

  const { name, email, password, eror, success, buttonText } = state;

  const handleChange = (name) => (e) => {
    setState({
      ...state,
      [name]: e.target.value,
      error: "",
      success: "",
      buttonText: "Register",
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // console.table({ name, email, password });
    setState({
      ...state,
      buttonText: "Registering",
    });
    axios
      .post(`http://localhost:8000/api/register`, {
        name,
        email,
        password,
      })
      .then((response) => {
        console.log(response);
        setState({
          ...state,
          name: "",
          email: "",
          password: "",
          buttonText: "Submitted",
          success: response.data.message,
        });
      })
      .catch((error) => {
        console.log(error);
        setState({
          ...state,
          buttonText: "Register",
          error: error.data.message,
        });
      });
  };

  const registerForm = () => (
    <form onSubmit={handleSubmit}>
      <div className="form-group">
        <input
          value={name}
          type="text"
          className="form-control"
          placeholder="Type your name"
          onChange={handleChange("name")}
        />
      </div>
      <div className="form-group">
        <input
          value={email}
          type="text"
          className="form-control"
          placeholder="Type your email"
          onChange={handleChange("email")}
        />
      </div>
      <div className="form-group">
        <input
          valu={password}
          type="password"
          className="form-control"
          placeholder="Type your password"
          onChange={handleChange("password")}
        />
      </div>
      <div className="form-group">
        <button className="btn btn-outline-warning">{state.buttonText}</button>
      </div>
    </form>
  );

  return (
    <Layout>
      {success && showSuccessMessage(success)}
      <div className="col-md6 offset-md-3">
        <h1>Register</h1>
        <br />
        {registerForm()}
        <hr />
        {JSON.stringify(state)}
      </div>
    </Layout>
  );
};

export default Register;
