import Layout from "../../../components/layout.component";
import withAdmin from "../../withAdmin";
import { useState, useEffect } from "react";
import axios from "axios";
import { API } from "../../../config";
import { showSuccessMessage, showErrorMessage } from "../../../utils/alerts";

const Create = ({ user, token }) => {
  const [state, setState] = useState({
    name: "",
    content: "",
    error: "",
    success: "",
    formData: process.browser && new FormData(),
    buttonText: "Create",
    imageUploadText: "Upload image",
  });

  const {
    name,
    content,
    success,
    error,
    formData,
    buttonText,
    imageUploadText,
  } = state;

  const handleChange = (name) => (e) => {
    const value = name === "image" ? e.target.files[0] : e.target.value;
    const imageName = name === "image" ? e.target.files[0] : "Upload image";
    formData.set(name, value);
    setState({
      ...state,
      [name]: value,
      error: "",
      success: "",
      imageUploadText: imageName,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setState({
      ...state,
      buttonText: "Creating",
    });
    try {
      console.log(`response formData`, ...formData);
      const response = await axios.post(
        `${API}/category`,
        formData,
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      console.log(`response category`, response);
      setState({
        ...state,
        name: "",
        formData: new FormData(),
        buttonText: "Created",
        imageUploadText: "Upload image",
      });
    } catch (error) {
      console.log(`response category error`, error);
      setState({
        ...state,
        name: "",
        buttonText: "Create",
        error: error.res,
      });
    }
  };

  const createCategoryForm = () => (
    <form onSubmit={handleSubmit}>
      <div className="form-group">
        <label className="text-mited">Name</label>
        <input
          onChange={handleChange("name")}
          value={name}
          type="text"
          className="form-control"
        ></input>
      </div>
      <div className="form-group">
        <label className="text-mited">Content</label>
        <textarea
          onChange={handleChange("content")}
          value={content}
          className="form-control"
        ></textarea>
      </div>
      <div className="form-group">
        <label className="btn btn-outline-secondary">
          Text
          <input
            onChange={handleChange("image")}
            type="file"
            className="form-control"
            accept="image/*"
            hidden
          ></input>
        </label>
      </div>
      <div>
        <button className="btn btn-outline-warning">{buttonText}</button>
      </div>
    </form>
  );

  return (
    <Layout>
      <div className="row">
        <div className="col-md-6 offset-md-3">
          <h1>Create category</h1>
          <br />
          {createCategoryForm()}
        </div>
      </div>
    </Layout>
  );
};

export default withAdmin(Create);
