import Layout from "../../../components/layout.component";
import withAdmin from "../../withAdmin";
import { useState, useEffect } from "react";
import axios from "axios";
import { API } from "../../../config";
import { showSuccessMessage, showErrorMessage } from "../../../utils/alerts";

const Create = () => {
  const [state, setState] = useState({
    name: "",
    content: "",
    error: "",
    success: "",
    formData: process.browser && new FormData(),
    buttonText: "Create",
    imageUploadText: "Upload image",
  });

  const {
    name,
    content,
    success,
    error,
    formData,
    buttonText,
    imageUploadText,
  } = state;

  const handleChange = (name) => (e) => {
    const value = name === "image" ? e.target.files[0] : e.target.value;
    const imageName = name === "image" ? e.target.files[0] : "Upload image";
    formData.set(name, value);
    setState({
      ...state,
      [name]: value,
      error: "",
      success: "",
      imageUploadText: imageName,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setState({
      ...state,
      buttonText: "Creating",
    });
    console.log(...formData);
  };

  const createCategoryForm = () => {
    <form onSubmit={handleSubmit}>
      <div className="form-group">
        <label className="text-mited">Name</label>
        <input
          onChange={handleChange("name")}
          value={name}
          type="text"
          className="form-control"
        ></input>
      </div>
      <div className="form-group">
        <label className="text-mited">Content</label>
        <textarea
          onChange={handleChange("content")}
          value={content}
          className="form-control"
        ></textarea>
      </div>
      <div className="form-group">
        <label className="text-mited">Image</label>
        <input
          onChange={handleChange("image")}
          type="file"
          className="form-control"
        ></input>
      </div>
      <div>
          <button className='btn btn-outline-warning'>{buttonText}</button>
      </div>
    </form>;
  };

  return (
    <Layout>
      <div className="row">
        <div className="col-md-6 offset-md-3">
          <h1>Create category</h1>
          <br />
          {createCategoryForm}
        </div>
      </div>
    </Layout>
  );
};

export default Create;
