import { useState, useEffect } from "react";
import Layout from "../../../components/layout.component";
import axios from "axios";
import { API } from "../../../config";

import { showSuccessMessage, showErrorMessage } from "../../../utils/alerts";

const Create = () => {
  const [state, setState] = useState({
    title: "",
    url: "",
    categories: [],
    loadedCategories: [],
    success: "",
    error: "",
    type: "",
    medium: "",
  });

  const {
    title,
    url,
    categories,
    loadedCategories,
    success,
    error,
    type,
    medium,
  } = state;

  useEffect(() => {
    loadCategories();
  }, [success]);

  const loadCategories = async () => {
    const response = await axios.get(`${API}/categories`);
    console.log("loadCategories:", response.data);
    setState({
      ...state,
      loadedCategories: response.data,
    });
  };

  const handleSubmit = async (e) => {};

  const handleTitleChange = (e) => {};

  const handleUrlChange = (e) => {};

  const submitLinkForm = () => (
    <form onSubmit={handleSubmit}>
      <div className="form-group">
        <label className="text-muted">Title</label>
        <input
          type="text"
          className="form-control"
          onChange={handleTitleChange}
          value={title}
        />
      </div>
      <div className="form-group">
        <label className="text-muted">URL</label>
        <input
          type="url"
          className="form-control"
          onChange={handleUrlChange}
          value={url}
        />
      </div>
      <div>
        <button className="btn btn-outline-warning" type="submit"></button>
      </div>
    </form>
  );

  return (
    <Layout>
      <div className="row">
        <div className="col-md-12">
          <h1>Submit Link/URL</h1>
          <br />
          <div className="col-md-4">xxx</div>
          <div className="col-md-8">{submitLinkForm()}</div>
        </div>
      </div>
    </Layout>
  );
};

export default Create;
